mapper.select:
select
  date_of, confirmed_count, cured_count, confirmed_incr, input_incr, cured_incr
from t_daily u


        and date_of = #{dateOf}


        and confirmed_count = #{confirmedCount}


        and cured_count = #{curedCount}


        and confirmed_incr = #{confirmedIncr}


        and input_incr = #{inputIncr}


        and cured_incr = #{curedIncr}
